        [Test]
        public void DecryptStringTest()
        {
            string decryptedString = cryptographyService.DecryptString(encryptedString);
            Assert.That(decryptedString, Is.EqualTo(notEncryptedString));
        }

        [Test]
        public void EncryptDecryptStringTest()
        {
            string encString = cryptographyService.EncryptString(notEncryptedString);
            string notEncString = cryptographyService.DecryptString(encString);
            Assert.That(notEncryptedString, Is.EqualTo(notEncString));
        }

        [Test]
        public void GetPublicKeyTest()
        {
            Assert.That(cryptographyService.GetPublicKey(), Is.EqualTo(publicKey));
        }

        [Test]
        public void PublicKeyCorrectLengthTest()
        {
            Assert.That(cryptographyService.GetPublicKey().Length, Is.EqualTo(publicKeyLength));
